import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import api from '../../services/api';

const initialState = {
  exams: [],
  currentExam: null,
  loading: false,
  error: null,
  timeRemaining: null,
};

export const fetchExam = createAsyncThunk(
  'exam/fetchExam',
  async (examId) => {
    const response = await api.getExam(examId);
    return response.data;
  }
);

export const createExam = createAsyncThunk(
  'exam/createExam',
  async (examData) => {
    const response = await api.createExam(examData);
    return response.data;
  }
);

export const submitExam = createAsyncThunk(
  'exam/submitExam',
  async ({ examId, answers }) => {
    const response = await api.submitExam(examId, answers);
    return response.data;
  }
);

export const examSlice = createSlice({
  name: 'exam',
  initialState,
  reducers: {
    fetchExamsStart: (state) => {
      state.loading = true;
      state.error = null;
    },
    fetchExamsSuccess: (state, action) => {
      state.loading = false;
      state.exams = action.payload;
    },
    fetchExamsFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
    setCurrentExam: (state, action) => {
      state.currentExam = action.payload;
    },
    createExamSuccess: (state, action) => {
      state.exams.push(action.payload);
    },
    updateExamSuccess: (state, action) => {
      const index = state.exams.findIndex(exam => exam.id === action.payload.id);
      if (index !== -1) {
        state.exams[index] = action.payload;
      }
    },
    deleteExamSuccess: (state, action) => {
      state.exams = state.exams.filter(exam => exam.id !== action.payload);
    },
    setTimeRemaining: (state, action) => {
      state.timeRemaining = action.payload;
    },
  },
  extraReducers: (builder) => {
    // fetchExam
    builder
      .addCase(fetchExam.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchExam.fulfilled, (state, action) => {
        state.loading = false;
        state.currentExam = action.payload;
      })
      .addCase(fetchExam.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      })
      // createExam
      .addCase(createExam.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(createExam.fulfilled, (state, action) => {
        state.loading = false;
        state.exams.push(action.payload);
      })
      .addCase(createExam.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      })
      // submitExam
      .addCase(submitExam.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(submitExam.fulfilled, (state, action) => {
        state.loading = false;
        state.currentExam = action.payload;
      })
      .addCase(submitExam.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      });
  },
});

export const {
  fetchExamsStart,
  fetchExamsSuccess,
  fetchExamsFailure,
  setCurrentExam,
  createExamSuccess,
  updateExamSuccess,
  deleteExamSuccess,
  setTimeRemaining,
} = examSlice.actions;

export default examSlice.reducer;
