import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  exams: [],
  currentExam: null,
  loading: false,
  error: null,
};

export const examSlice = createSlice({
  name: 'exam',
  initialState,
  reducers: {
    fetchExamsStart: (state) => {
      state.loading = true;
      state.error = null;
    },
    fetchExamsSuccess: (state, action) => {
      state.loading = false;
      state.exams = action.payload;
    },
    fetchExamsFailure: (state, action) => {
      state.loading = false;
      state.error = action.payload;
    },
    setCurrentExam: (state, action) => {
      state.currentExam = action.payload;
    },
    createExamSuccess: (state, action) => {
      state.exams.push(action.payload);
    },
    updateExamSuccess: (state, action) => {
      const index = state.exams.findIndex(exam => exam.id === action.payload.id);
      if (index !== -1) {
        state.exams[index] = action.payload;
      }
    },
    deleteExamSuccess: (state, action) => {
      state.exams = state.exams.filter(exam => exam.id !== action.payload);
    },
  },
});

export const {
  fetchExamsStart,
  fetchExamsSuccess,
  fetchExamsFailure,
  setCurrentExam,
  createExamSuccess,
  updateExamSuccess,
  deleteExamSuccess,
} = examSlice.actions;

export default examSlice.reducer;
