import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useDispatch, useSelector } from 'react-redux';
import {
  Box,
  Button,
  Typography,
  Paper,
  Grid,
  Card,
  CardContent,
  CardActions,
} from '@mui/material';
import { Add as AddIcon } from '@mui/icons-material';
import { fetchExamsStart, fetchExamsSuccess, fetchExamsFailure } from '../store/slices/examSlice';
import { getExams } from '../services/api';

const Dashboard = () => {
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const { user } = useSelector((state) => state.auth);
  const { exams, loading } = useSelector((state) => state.exam);

  useEffect(() => {
    const loadExams = async () => {
      dispatch(fetchExamsStart());
      try {
        const response = await getExams();
        dispatch(fetchExamsSuccess(response.data));
      } catch (error) {
        dispatch(fetchExamsFailure(error.message));
      }
    };
    loadExams();
  }, [dispatch]);

  const isProctor = user?.role === 'PROCTOR';

  return (
    <Box>
      <Box sx={{ mb: 4, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Typography variant="h4">
          {isProctor ? 'Manage Exams' : 'Available Exams'}
        </Typography>
        {isProctor && (
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => navigate('/create-exam')}
          >
            Create New Exam
          </Button>
        )}
      </Box>

      {loading ? (
        <Typography>Loading exams...</Typography>
      ) : (
        <Grid container spacing={3}>
          {exams.map((exam) => (
            <Grid item xs={12} md={6} lg={4} key={exam.id}>
              <Card>
                <CardContent>
                  <Typography variant="h6" gutterBottom>
                    {exam.title}
                  </Typography>
                  <Typography variant="body2" color="text.secondary" paragraph>
                    {exam.description}
                  </Typography>
                  <Typography variant="body2">
                    Duration: {exam.durationMinutes} minutes
                  </Typography>
                  <Typography variant="body2">
                    Questions: {exam.questions.length}
                  </Typography>
                </CardContent>
                <CardActions>
                  {isProctor ? (
                    <>
                      <Button size="small" onClick={() => navigate(`/edit-exam/${exam.id}`)}>
                        Edit
                      </Button>
                      <Button size="small" color="error">
                        Delete
                      </Button>
                    </>
                  ) : (
                    <Button
                      size="small"
                      color="primary"
                      onClick={() => navigate(`/take-exam/${exam.id}`)}
                    >
                      Take Exam
                    </Button>
                  )}
                </CardActions>
              </Card>
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
};

export default Dashboard;
